
import os
import sys
import math

class MyOperations:

    def __init__(self, number1, number2):
        self.number1 = number1
        self.number2 = number2

    def my_sum(self,number3=0):
        return self.number1 + self.number2 + number3

    def my_mult(self,number3=1):
        return self.number1 * self.number2 * number3


